const { Command } = require('klasa');
const { monkeyfacts, dogfacts, compliments, horsefacts } = require('../../array.js')
module.exports = class extends Command {

	constructor(...args) {
		super(...args, {
			description: 'Array count',
		});
	}

	run(message) {
        return message.sendMessage(`Array count is: monkeys:${monkeyfacts.length}, Dogs:${dogfacts.length}, Compliments:${compliments.length}, Horse:${horsefacts.length} `);
	}

};
